import React, { Fragment, useEffect, useState } from 'react';
import './profile.css';

function Profile(props) {
    const [basicInfo, setBasicInfo] = useState({});
    const [user, setUser] = useState({})
    let url = `http://localhost:8000/api/users/${user.id}/`;

    useEffect(() => {
        setUser(JSON.parse(localStorage.getItem('user')))
        fetch(url, {
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json'
            },
            method: "PUT",
            body: JSON.stringify(basicInfo)
        })
        .then(res => console.log(res))
        .catch(error => console.log(error))
    }, [basicInfo])

    function submitHandler(event) {
        event.preventDefault();
        setBasicInfo({
            firstName: event.target.children[1].value,
            lastName: event.target.children[3].value,
            email: (event.target.children[5].value === event.target.children[7].value) ? event.target.children[5].value : null,
            password: (event.target.children[9].value === event.target.children[11].value) ? event.target.children[9].value : null
        })
        setTimeout(()=> {
            event.target.children[1].value = '';
            event.target.children[3].value = '';
            event.target.children[5].value = '';
            event.target.children[7].value = '';
            event.target.children[9].value = ''; 
        },1000)
    }
console.log(basicInfo)
    return (
        <div className='profileContainer' >
            <div className='basicInfoContainer' >
                <h4>First Name</h4>
                <p>{user.firstName}</p>
                <h4>Last Name</h4>
                <p>{user.lastName}</p>
                <h4>Email</h4>
                <p>{user.email}</p>
                <h4>Password</h4>
                <p>{user.password}</p>
            </div>

            <div className='editBasicInfoContainer' >
                <form onSubmit={submitHandler}>
                    <label>First Name</label>
                    <input placeholder='Enter your first name' ></input>

                    <label>Last Name</label>
                    <input placeholder='Enter your last name' ></input>

                    <label>Email</label>
                    <input placeholder='Enter your email address' ></input>

                    <label>Confirm Email</label>
                    <input placeholder='Confirm your email address' ></input>

                    <label>Password</label>
                    <input placeholder='Enter a password' ></input>

                    <label>Confirm Password</label>
                    <input placeholder='Confirm your password' ></input>

                    <button type='submit'>Save</button>
                </form>
            </div>

            <div className='addtionalInfoContainer' >

            </div>

            <div className='editAddtionalInfoContainer' >

            </div>

            <div className='achievementContainer' >

            </div>

        </div>
    );
}

export default Profile;
