import React, { Fragment } from 'react';
import './blog.css';

function Blog(props) {
    return (
        <Fragment>

            <div className='blogFormContainer' >
                <form className='blogForm' onSubmit={props.blogFormSubmitHandler}>
                    <div className='formItem' >
                        <label>Title</label>
                        <br />
                        <input></input>
                    </div>
                    <div className='formItem' >
                        <label>Description</label>
                        <br />
                        <input></input>
                    </div>
                    <div className='formItem' >
                        <label>Aurthor</label>
                        <br />
                        <input></input>
                    </div>
                </form>
                <button>Send</button>
            </div>

            <div className='blogListContainer' >

            {
                props.blogList.map(blogItem =>  {
                    return (
                        <div>
                            <h4>{blogItem.title}</h4>
                            <p>{blogItem.description}</p>
                            <p>By: {blogItem.name}</p>
                            {blogItem.date}
                        </div>
                    )
                })
            }

            </div>

        </Fragment>
    );
}

export default Blog;
