import React, {useState, useEffect} from 'react';
import { Fragment } from 'react';
import './catagorycontent.css';

function CatagoryContent(props) {

    const [subject, setSubject] = useState([]);
    const [subjectContent, setSubjectContent] = useState(``);


    const url = 'http://localhost:8000/api/subjects';

    useEffect(()=> {
        fetch(url)
        .then(res => res.json())
        .then(res => {
            setSubject(res)
            setSubjectContent(res.focusContent)
        })
    },[])



    return (
        <Fragment>
            <div className='subHeader'>
                <h1>HTML</h1>
            </div>

            {
            subject.map(content => {
                const title = content.focus;
                const description = content.focusDefinition;
                const subjectContent = content.focusContent;
                return (
                    <Fragment>
                        <h1>{title}</h1>
                        <h3>{description}</h3>
                        {`${subjectContent}`}
                    </Fragment>
                );
            })}


        </Fragment>
    );
}

export default CatagoryContent;
