import React, { Fragment, useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './signin.css'

const url = 'http://localhost:8000/api/users/';

function SignIn(props) {

    const [login, setLogin] = useState([]);
    const [users, setUsers] = useState([]);

    function submitHandler(event) {
        event.preventDefault();
        setLogin({
            email: event.target.children[0].children[0].value,
            password: event.target.children[0].children[1].value
        })
    }


    let found = users.find(user => (user.email === login.email || user.password === login) ? console.log('Matched') : console.log('Not Found'))

    console.log(found)
    console.log(login)

    useEffect(() => {
        fetch(url, {
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json'
            },
            method: "GET"
        })
        .then(res => res.json())
        .then(res => setUsers(res))
        .catch(error => console.log(error))
    },[login])


    return (
        <Fragment>
            <div className='signInContainer'>
                <form className='signInForm' onSubmit={submitHandler}>

                    <div className='signInPasswordContainer'>
                        <input className='signInInput' id='signInEmail' placeholder='Enter your email'></input>
                        <input   input className='signInInput' id='signInPassword' placeholder='Enter your password'></input>
                    </div>

                    <button type='submit' id='signInButton'><Link to='dashboard'>Submit</Link></button>

                </form>
            </div>
            <div className='signInGreeting'>
                <h2>Sign in</h2>
                <p>Welcome, back!</p>
            </div>
        </Fragment>
    );
}

export default SignIn;
